// src/googleSheet.js

/**
 * Отправляем POST-запрос на наш локальный прокси (/api/sheet),
 * который переадресует его на Google Apps Script и вернёт ответ.
 */
const SHEET_PROXY_URL = "/api/sheet";

async function postToSheet(action, payload) {
  const res = await fetch(SHEET_PROXY_URL, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ action, payload }),
  });

  if (!res.ok) {
    throw new Error(`Sheet API error ${res.status}`);
  }
  return res.json();
}

/**
 * Добавить новую задачу в Google Sheets
 * @param {object} task — { id, title, description, status, startDate, dueDate, priority, labels }
 */
export function addTaskToSheet(task) {
  return postToSheet("add", task);
}

/**
 * Обновить существующую задачу в Google Sheets
 * @param {object} task — { id, title, description, status, startDate, dueDate, priority, labels }
 */
export function updateTaskInSheet(task) {
  return postToSheet("update", task);
}

/**
 * Удалить задачу из Google Sheets по ID
 * @param {string|number} id
 */
export function deleteTaskFromSheet(id) {
  return postToSheet("delete", { id });
}
