// src/Components/Card/CardInfo/CardInfo.jsx
import React, { useEffect, useState } from "react";
import debounce from "lodash/debounce";
import { X } from "react-feather";
import { SquarePen, Plus, Trash, Clock } from "lucide-react";

import Modal from "../../Modal/Modal";
import Editable from "../../Editable/Editable";
import "./CardInfo.css";

function CardInfo(props) {
  const { id, title, description, startDate: initStart, date: initDue, labels, tasks } = props.card;

  const [localValues, setLocalValues] = useState({
    title: title || "",
    description: description || "",
    status: props.card.status || "На проверке",
    startDate: initStart || new Date().toISOString().split("T")[0],
    dueDate: initDue || "",
    labels: labels || [],
    tasks: tasks || [],
  });

  // синхронное обновление при изменении localValues
  useEffect(() => {
    const debounced = debounce(() => {
      props.updateCard(props.boardId, id, {
        ...props.card,
        ...localValues,
      });
    }, 500);
    debounced();
    return () => {
      debounced.flush();
      debounced.cancel();
    };
  }, [localValues]);

  // обновления
  const updateField = (field) => (val) =>
    setLocalValues((prev) => ({ ...prev, [field]: val }));

  // задачи чеклиста
  const addTask = (text) => {
    if (!text.trim()) return;
    setLocalValues((p) => ({
      ...p,
      tasks: [...p.tasks, { id: Date.now(), text, completed: false }],
    }));
  };
  const toggleTask = (taskId) =>
    setLocalValues((p) => ({
      ...p,
      tasks: p.tasks.map((t) =>
        t.id === taskId ? { ...t, completed: !t.completed } : t
      ),
    }));
  const removeTask = (taskId) =>
    setLocalValues((p) => ({
      ...p,
      tasks: p.tasks.filter((t) => t.id !== taskId),
    }));

  // вычисление прогресса
  const tasksCount = localValues.tasks.length;
  const tasksCompleted = localValues.tasks.filter((t) => t.completed).length;
  const progressPercent = tasksCount === 0 ? 0 : Math.round((tasksCompleted / tasksCount) * 100);

  // динамический приоритет по времени
  const computePriority = () => {
    const { startDate, dueDate } = localValues;
    if (!dueDate) return "Urgent";
    const start = new Date(startDate);
    const due = new Date(dueDate);
    const now = new Date();
    const totalMs = due - start;
    const leftMs = due - now;
    const ratio = leftMs / totalMs;
    if (ratio > 0.5) return "Low";          // несрочно
    if (ratio > 0.25) return "Medium";       // поторопитесь
    return "Urgent";                         // срочно
  };
  const displayPriority = computePriority();

  const getStatusStyles = (prio) => {
    switch (prio) {
      case "Low":
        return { backgroundColor: "var(--green-300)", color: "var(--green-700)" };
      case "Medium":
        return { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" };
      case "Urgent":
      default:
        return { backgroundColor: "var(--red-300)", color: "var(--red-700)" };
    }
  };

  return (
    <Modal onClose={props.onClose}>
      <div className="cardinfo_container">
        <button className="cardinfo_close" onClick={props.onClose}>
          <X size={24} />
        </button>

        <h2 className="cardinfo_title">
          <Editable defaultValue={localValues.title} onSubmit={updateField("title")} />
        </h2>

        <div className="cardinfo_description">
          <Editable
            defaultValue={localValues.description}
            onSubmit={updateField("description")}
          />
        </div>

        <div className="cardinfo_fields">
          <div className="field">
            <span>Статус</span>
            <span className="static" style={getStatusStyles(localValues.status)}>
              {localValues.status}
            </span>
          </div>
          <div className="field">
            <span>Дата начала</span>
            <input
              type="date"
              value={localValues.startDate}
              onChange={(e) => updateField("startDate")(e.target.value)}
            />
          </div>
          <div className="field">
            <span>Дата завершения</span>
            <input
              type="date"
              value={localValues.dueDate}
              onChange={(e) => updateField("dueDate")(e.target.value)}
            />
          </div>
          <div className="field">
            <span>Приоритет</span>
            <span className="static" style={getStatusStyles(displayPriority)}>
              {displayPriority === "Low" ? "Несрочно"
                : displayPriority === "Medium" ? "Поторопитесь"
                : "Срочно"}
            </span>
          </div>
        </div>

        <div className="cardinfo_labels">
          <Editable
            defaultValue="Добавить тег"
            shortLine
            onSubmit={(text) => {
              setLocalValues((prev) => ({
                ...prev,
                labels: [...prev.labels, { text, color: "blue" }],
              }));
            }}
          />
          {/* отобразить метки... */}
        </div>

        <div className="cardinfo_tabs">
          <span className="active">Чеклист</span>
          <span>Файлы</span>
          <span>Комментарии</span>
          <span>Активность</span>
        </div>

        <div className="cardinfo_checklist">
          <div className="progress-bar">
            <div
              className="fill"
              style={{ width: `${progressPercent}%` }}
            />
          </div>
          <div className="tasks">
            {localValues.tasks.map((task) => (
              <div key={task.id} className="task-row">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleTask(task.id)}
                />
                <span className={task.completed ? "done" : ""}>{task.text}</span>
                <Trash
                  className="task-trash"
                  onClick={() => removeTask(task.id)}
                />
              </div>
            ))}
            <Editable
              defaultValue="Добавить задачу"
              onSubmit={addTask}
            />
          </div>
        </div>
      </div>
    </Modal>
  );
}

export default CardInfo;
