import React, { useEffect, useState } from "react";
import debounce from "lodash/debounce";
import {
  X,
  Calendar,
  CheckSquare,
  Plus,
  Edit3,
} from "react-feather";

import Modal from "../../Modal/Modal";
import Editable from "../../Editabled/Editable";
import "./CardInfo.css";

function CardInfo(props) {
  const { id, title, description, date, labels, tasks } = props.card;

  const [localValues, setLocalValues] = useState({
    title: title || "",
    description: description || "",
    status: props.card.status || "На проверке", // нередактируем
    startDate: "",
    dueDate: date || "",
    priority: "Urgent",
    labels: labels || [],
    tasks: tasks || [],
  });

  // Debounce-сохранение
  useEffect(() => {
    const debounced = debounce(() => {
      props.updateCard(props.boardId, id, {
        ...props.card,
        ...localValues,
      });
    }, 500);

    debounced();
    return () => {
      debounced.flush();
      debounced.cancel();
    };
  }, [localValues, props, id]);

  // Handlers
  const updateTitle = (val) => setLocalValues((p) => ({ ...p, title: val }));
  const updateDescription = (val) =>
    setLocalValues((p) => ({ ...p, description: val }));
  const updateStartDate = (val) =>
    setLocalValues((p) => ({ ...p, startDate: val }));
  const updateDueDate = (val) =>
    setLocalValues((p) => ({ ...p, dueDate: val }));
  const updatePriority = (val) =>
    setLocalValues((p) => ({ ...p, priority: val }));

  // Labels
  const [newLabelText, setNewLabelText] = useState("");
  const [selectedColor, setSelectedColor] = useState("#fccb00");
  const addLabel = () => {
    if (!newLabelText.trim()) return;
    const newLabel = { text: newLabelText.trim(), color: selectedColor };
    setLocalValues((prev) => ({
      ...prev,
      labels: [...prev.labels, newLabel],
    }));
    setNewLabelText("");
  };
  const removeLabel = (index) => {
    const updated = [...localValues.labels];
    updated.splice(index, 1);
    setLocalValues((prev) => ({ ...prev, labels: updated }));
  };

  // Checklist
  const addTask = (text) => {
    if (!text.trim()) return;
    const newTask = { id: Date.now() + Math.random(), text, completed: false };
    setLocalValues((prev) => ({
      ...prev,
      tasks: [...prev.tasks, newTask],
    }));
  };
  const toggleTask = (taskId) => {
    const updated = localValues.tasks.map((t) =>
      t.id === taskId ? { ...t, completed: !t.completed } : t
    );
    setLocalValues((prev) => ({ ...prev, tasks: updated }));
  };
  const removeTask = (taskId) => {
    const updated = localValues.tasks.filter((t) => t.id !== taskId);
    setLocalValues((prev) => ({ ...prev, tasks: updated }));
  };

  const tasksCount = localValues.tasks.length;
  const tasksCompleted = localValues.tasks.filter((t) => t.completed).length;
  const progressPercent =
    tasksCount === 0 ? 0 : Math.round((tasksCompleted / tasksCount) * 100);

  let progressColor = "#e74c3c";
  if (progressPercent >= 33 && progressPercent < 100) {
    progressColor = "#f1c40f";
  } else if (progressPercent === 100) {
    progressColor = "#2ecc71";
  }

  return (
    <Modal onClose={props.onClose}>
      <div className="cardinfo_container">
        <div className="cardinfo_close-btn" onClick={props.onClose}>
          <X size={24} />
        </div>

        {/* Заголовок */}
        <h2 className="cardinfo_title">
          <Editable
            defaultValue={localValues.title}
            placeholder="Введите заголовок..."
            onSubmit={updateTitle}
          />
        </h2>

        {/* Описание */}
        <p className="cardinfo_description">
          <Editable
            defaultValue={localValues.description}
            placeholder="Введите описание..."
            onSubmit={updateDescription}
          />
        </p>

        {/* Поля: табличный вид */}
        <div className="cardinfo_table-fields">
          {/* Статус: нередактируем */}
          <div className="row">
            <div className="row_label">Статус</div>
            <div className="row_value cardinfo_static-value">
              {localValues.status}
            </div>
          </div>

          {/* Дата начала */}
          <div className="row">
            <div className="row_label">Дата начала</div>
            <div className="row_value">
              <Calendar size={16} />
              <input
                type="date"
                value={localValues.startDate || ""}
                onChange={(e) => updateStartDate(e.target.value)}
              />
            </div>
          </div>

          {/* Дата завершения */}
          <div className="row">
            <div className="row_label">Дата завершения</div>
            <div className="row_value">
              <Calendar size={16} />
              <input
                type="date"
                value={localValues.dueDate || ""}
                onChange={(e) => updateDueDate(e.target.value)}
              />
            </div>
          </div>

          {/* Приоритет */}
          <div className="row">
            <div className="row_label">Приоритет</div>
            <div className="row_value cardinfo_pill">
              <Editable
                defaultValue={localValues.priority}
                placeholder="Urgent"
                onSubmit={updatePriority}
              />
            </div>
          </div>

          {/* Теги */}
          <div className="row">
            <div className="row_label">Теги</div>
            <div className="row_value cardinfo_tags">
              <div className="cardinfo_label-colors">
                {["#fccb00", "#76defe", "#b5f8b8", "#fa9999"].map((color, idx) => (
                  <div
                    key={idx}
                    className="cardinfo_color-circle"
                    style={{ backgroundColor: color }}
                    onClick={() => setSelectedColor(color)}
                  />
                ))}
              </div>
              <div className="cardinfo_label-input">
                <input
                  type="text"
                  placeholder="Введите название тега"
                  value={newLabelText}
                  onChange={(e) => setNewLabelText(e.target.value)}
                />
                <button onClick={addLabel}>
                  <Plus size={14} />
                </button>
              </div>
              <div className="cardinfo_labels-list">
                {localValues.labels.map((labelObj, i) => (
                  <div
                    key={i}
                    className="cardinfo_label-pill"
                    style={{ backgroundColor: labelObj.color }}
                  >
                    <span>{labelObj.text}</span>
                    <Edit3
                      className="cardinfo_label-remove"
                      size={14}
                      onClick={() => removeLabel(i)}
                    />
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Вкладки слева */}
        <div className="cardinfo_tabs">
          <span className="cardinfo_tab-active">Чеклист</span>
          <span className="cardinfo_tab">Файлы</span>
          <span className="cardinfo_tab">Комментарии</span>
          <span className="cardinfo_tab">Активность</span>
        </div>

        {/* Чеклист */}
        <div className="cardinfo_checklist-header">
          <h3>Чеклист ({tasksCompleted}/{tasksCount})</h3>
        </div>
        <div className="cardinfo_progress-bar">
          <div
            className="cardinfo_progress-fill"
            style={{ width: `${progressPercent}%`, backgroundColor: progressColor }}
          />
        </div>
        <div className="cardinfo_tasks">
          {localValues.tasks.map((task) => (
            <div key={task.id} className="cardinfo_task-item">
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTask(task.id)}
              />
              <span>{task.text}</span>
              <Edit3
                className="cardinfo_task-remove"
                size={14}
                onClick={() => removeTask(task.id)}
              />
            </div>
          ))}
        </div>
        <Editable text="Add a Task" placeholder="Введите задачу..." onSubmit={addTask} />
      </div>
    </Modal>
  );
}

export default CardInfo;
