// src/Components/Card/CardInfo/CardInfo.jsx
import React, { useEffect, useState } from "react";
import debounce from "lodash/debounce";
import { X, Edit3 } from "react-feather";
import { SquarePen, Plus, Trash, Clock } from "lucide-react";

import Modal from "../../Modal/Modal";
import Editable from "../../Editabled/Editable";
import "./CardInfo.css";

function CardInfo(props) {
  // Берём startDate и date (dueDate) из props.card
  const {
    id,
    title,
    description,
    status,
    labels: initLabels,
    tasks: initTasks,
    startDate: initStart,
    date: initDue,
  } = props.card;

  // Локальный стейт, стартовая дата — либо та, что в карточке, либо сегодня
  const [localValues, setLocalValues] = useState({
    title: title || "",
    description: description || "",
    status: status || "На проверке",
    startDate: initStart || new Date().toISOString().split("T")[0],
    dueDate: initDue || "",
    labels: initLabels || [],
    tasks: initTasks || [],
  });

  // Debounced update
  useEffect(() => {
    const job = debounce(() => {
      props.updateCard(props.boardId, id, {
        ...props.card,
        ...localValues,
      });
    }, 500);
    job();
    return () => {
      job.flush();
      job.cancel();
    };
  }, [localValues]);

  const updateField = (field) => (value) =>
    setLocalValues((prev) => ({ ...prev, [field]: value }));

  // Методы для тегов
  const [newLabelText, setNewLabelText] = useState("");
  const [selectedColor, setSelectedColor] = useState("yellow");
  const addLabel = () => {
    const txt = newLabelText.trim();
    if (!txt) return;
    setLocalValues((p) => ({
      ...p,
      labels: [...p.labels, { text: txt, color: selectedColor }],
    }));
    setNewLabelText("");
  };
  const removeLabel = (idx) =>
    setLocalValues((p) => ({
      ...p,
      labels: p.labels.filter((_, i) => i !== idx),
    }));

  // Методы для чеклиста (вёрстка оставил вашу оригинальную)
  const addTask = (text) => {
    const t = text.trim();
    if (!t) return;
    setLocalValues((p) => ({
      ...p,
      tasks: [...p.tasks, { id: Date.now(), text: t, completed: false }],
    }));
  };
  const toggleTask = (taskId) =>
    setLocalValues((p) => ({
      ...p,
      tasks: p.tasks.map((t) =>
        t.id === taskId ? { ...t, completed: !t.completed } : t
      ),
    }));
  const removeTask = (taskId) =>
    setLocalValues((p) => ({
      ...p,
      tasks: p.tasks.filter((t) => t.id !== taskId),
    }));

  // Прогресс
  const tasksCount = localValues.tasks.length;
  const tasksCompleted = localValues.tasks.filter((t) => t.completed).length;
  const progressPercent = tasksCount
    ? Math.round((tasksCompleted / tasksCount) * 100)
    : 0;

  // Приоритет по дате окончания
  let priorityLabel, priorityStyle;
  if (!localValues.dueDate) {
    priorityLabel = "Не задано";
    priorityStyle = {
      backgroundColor: "var(--secondary-background)",
      color: "var(--text-muted)",
    };
  } else {
    const start = new Date(localValues.startDate);
    const due = new Date(localValues.dueDate);
    const now = new Date();
    const total = due - start;
    const left = due - now;
    const ratio = left / total;
    if (ratio > 0.5) {
      priorityLabel = "Несрочно";
      priorityStyle = {
        backgroundColor: "var(--green-300)",
        color: "var(--green-700)",
      };
    } else if (ratio > 0.25) {
      priorityLabel = "Поторопитесь";
      priorityStyle = {
        backgroundColor: "var(--yellow-300)",
        color: "var(--yellow-700)",
      };
    } else {
      priorityLabel = "Срочно";
      priorityStyle = {
        backgroundColor: "var(--red-300)",
        color: "var(--red-700)",
      };
    }
  }

  // Стили для статики статуса, оставил ваш вариант
  const getStatusStyles = (st) => {
    switch (st) {
      case "Очередь":
        return { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" };
      case "В работе":
        return { backgroundColor: "var(--blue-300)", color: "var(--blue-700)" };
      case "На проверке":
        return { backgroundColor: "var(--red-300)", color: "var(--red-700)" };
      case "Готово":
        return { backgroundColor: "var(--green-300)", color: "var(--green-700)" };
      default:
        return { backgroundColor: "var(--secondary-background)", color: "var(--text-muted)" };
    }
  };

  return (
    <Modal onClose={props.onClose}>
      <div className="cardinfo_container">
        {/* Close */}
        <div className="cardinfo_close-btn" onClick={props.onClose}>
          <X size={24} />
        </div>

        {/* Заголовок */}
        <h2 className="cardinfo_title">
          <Editable
            defaultValue={localValues.title}
            placeholder="Введите заголовок..."
            onSubmit={updateField("title")}
          />
        </h2>

        {/* Описание */}
        <div className="cardinfo_description">
          <Editable
            defaultValue={localValues.description}
            placeholder="Введите описание..."
            onSubmit={updateField("description")}
          />
        </div>

        {/* Табличные поля */}
        <div className="cardinfo_table-fields">
          {/* Статус */}
          <div className="row">
            <div className="row_label">Статус</div>
            <div
              className="row_value cardinfo_static-value"
              style={getStatusStyles(localValues.status)}
            >
              {localValues.status}
            </div>
          </div>

          {/* Дата начала */}
          <div className="row">
            <div className="row_label">Дата начала</div>
            <div className="row_value date-picker">
              <input
                type="date"
                value={localValues.startDate}
                onChange={(e) => updateField("startDate")(e.target.value)}
              />
            </div>
          </div>

          {/* Дата завершения */}
          <div className="row">
            <div className="row_label">Дата завершения</div>
            <div className="row_value date-picker">
              <input
                type="date"
                value={localValues.dueDate}
                onChange={(e) => updateField("dueDate")(e.target.value)}
              />
            </div>
          </div>

          {/* Приоритет */}
          <div className="row">
            <div className="row_label">Приоритет</div>
            <div className="row_value cardinfo_static-value" style={priorityStyle}>
              {priorityLabel}
            </div>
          </div>
        </div>

        {/* Теги */}
        <div className="row row-tags">
          <div className="row_label">Теги</div>
          <div className="row_value cardinfo_tags">
            <div className="cardinfo_label-input">
              <SquarePen className="editable_icon" />
              <input
                type="text"
                className="editable_input"
                placeholder="Название тега"
                value={newLabelText}
                onChange={(e) => setNewLabelText(e.target.value)}
              />
              {["yellow", "blue", "green", "red"].map((col) => (
                <div
                  key={col}
                  className="cardinfo_color-circle"
                  style={{ backgroundColor: `var(--${col}-300)` }}
                  onClick={() => setSelectedColor(col)}
                />
              ))}
              <button type="button" onClick={addLabel}>
                <Plus /> Добавить
              </button>
            </div>
            <div className="cardinfo_labels-list">
              {localValues.labels.map((lbl, i) => (
                <div
                  key={i}
                  className="cardinfo_label-pill"
                  style={{
                    backgroundColor: `var(--${lbl.color}-300)`,
                    color: `var(--${lbl.color}-700)`,
                  }}
                >
                  {lbl.text}
                  <Trash
                    className="cardinfo_label-remove"
                    size={14}
                    onClick={() => removeLabel(i)}
                  />
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Вкладки */}
        <div className="cardinfo_tabs">
          <span className="cardinfo_tab-active">Чеклист</span>
          <span className="cardinfo_tab">Файлы</span>
          <span className="cardinfo_tab">Комментарии</span>
          <span className="cardinfo_tab">Активность</span>
        </div>

        {/* === Чеклист (не трогал) === */}
        <div className="cardinfo_checklist-header">
          <h3>
            Чеклист ({tasksCompleted}/{tasksCount})
          </h3>
        </div>
        <div className="cardinfo_progress-bar">
          <div
            className="cardinfo_progress-fill"
            style={{ width: `${progressPercent}%` }}
          />
        </div>
        <div className="cardinfo_tasks">
          {localValues.tasks.map((task) => (
            <div key={task.id} className="cardinfo_task-item">
              <label className="custom-checkbox">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleTask(task.id)}
                />
                <span className="checkmark"></span>
              </label>
              <span>{task.text}</span>
              <Trash
                className="cardinfo_task-remove"
                size={14}
                onClick={() => removeTask(task.id)}
              />
            </div>
          ))}
        </div>
        <Editable
          defaultValue="Добавить задачу"
          placeholder="Введите задачу..."
          onSubmit={addTask}
        />
      </div>
    </Modal>
  );
}

export default CardInfo;
