// src/App.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { DragDropContext } from "react-beautiful-dnd";
import Board from "./Components/Board/Board";
import "./App.css";

export default function App() {
  const [boards, setBoards] = useState([]);
  const [darkTheme, setDarkTheme] = useState(
    localStorage.getItem("kanban-theme") === "dark"
  );

  // фильтр/сортировка/поиск состояния
  const [filterStatus, setFilterStatus] = useState("all");
  const [sortValue, setSortValue] = useState("none");
  const [searchQuery, setSearchQuery] = useState("");

  const [showSortMenu, setShowSortMenu] = useState(false);
  const [showFilterMenu, setShowFilterMenu] = useState(false);

  // колонки
  const statuses = ["Очередь", "В работе", "На проверке", "Готово"];
  const sortLabels = {
    titleAsc: "Title A→Z",
    titleDesc: "Title Z→A",
    dateAsc: "Date Asc",
    dateDesc: "Date Desc",
  };

  // загрузка начальных данных
  const fetchBoardData = () => {
    axios
      .get("http://localhost:3001/api/board")
      .then((res) => {
        const tasks = res.data.tasks || [];
        const grouped = statuses.map((st, i) => ({
          id: `${i + 1}`,
          title: st,
          cards: tasks.filter((t) => t.status === st),
        }));
        setBoards(grouped);
      })
      .catch((err) => console.error("Ошибка загрузки:", err));
  };
  useEffect(fetchBoardData, []);

  // тема
  useEffect(() => {
    localStorage.setItem("kanban-theme", darkTheme ? "dark" : "light");
  }, [darkTheme]);

  // клик вне меню
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (
        !e.target.closest(".menu-item") &&
        !e.target.closest(".dropdown-menu")
      ) {
        setShowSortMenu(false);
        setShowFilterMenu(false);
      }
    };
    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, []);

  // фильтр / поиск / сортировка
  const applyFilterSearchSort = (cards) => {
    let result = [...cards];
    if (filterStatus !== "all") {
      result = result.filter((c) => c.status === filterStatus);
    }
    if (searchQuery.trim()) {
      const q = searchQuery.toLowerCase();
      result = result.filter((c) => c.title.toLowerCase().includes(q));
    }
    if (sortValue === "titleAsc") {
      result.sort((a, b) => (a.title > b.title ? 1 : -1));
    } else if (sortValue === "titleDesc") {
      result.sort((a, b) => (a.title < b.title ? 1 : -1));
    } else if (sortValue === "dateAsc") {
      result.sort((a, b) => {
        if (!a.date) return 1;
        if (!b.date) return -1;
        return new Date(a.date) - new Date(b.date);
      });
    } else if (sortValue === "dateDesc") {
      result.sort((a, b) => {
        if (!a.date) return 1;
        if (!b.date) return -1;
        return new Date(b.date) - new Date(a.date);
      });
    }
    return result;
  };

  const displayedBoards = boards.map((b) => ({
    ...b,
    cards: applyFilterSearchSort(b.cards),
  }));

  // меню
  const handleSortClick = () => {
    setShowSortMenu((v) => !v);
    setShowFilterMenu(false);
  };
  const handleFilterClick = () => {
    setShowFilterMenu((v) => !v);
    setShowSortMenu(false);
  };
  const setSort = (val) => {
    setSortValue(val);
    setShowSortMenu(false);
  };
  const setFilter = (val) => {
    setFilterStatus(val);
    setShowFilterMenu(false);
  };

  // добавление карточки — сюда приходит объект cardData со всеми полями
  const addCardHandler = (boardId, cardData) => {
    const board = boards.find((b) => b.id === boardId);
    if (!board) return;
    const newCard = {
      ...cardData,
      status: board.title,
    };
    setBoards((bs) =>
      bs.map((b) =>
        b.id === boardId ? { ...b, cards: [...b.cards, newCard] } : b
      )
    );
    // отправляем на сервер
    axios.post("http://localhost:3001/api/addTask", { card: newCard });
  };

  // обновление карточки — обновляем все поля
  const updateCard = (boardId, cardId, updatedCard) => {
    setBoards((bs) =>
      bs.map((b) =>
        b.id === boardId
          ? {
              ...b,
              cards: b.cards.map((c) => (c.id === cardId ? updatedCard : c)),
            }
          : b
      )
    );
    axios.post("http://localhost:3001/api/editTask", { card: updatedCard })
      .catch((err) => console.error("Ошибка обновления:", err));
  };

  // удаление
  const removeCard = (boardId, cardId) => {
    setBoards((bs) =>
      bs.map((b) =>
        b.id === boardId
          ? { ...b, cards: b.cards.filter((c) => c.id !== cardId) }
          : b
      )
    );
    // можно axios.delete...
  };

  // Drag & Drop
  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;
    const srcIdx = boards.findIndex((b) => b.id === source.droppableId);
    const dstIdx = boards.findIndex((b) => b.id === destination.droppableId);
    if (srcIdx < 0 || dstIdx < 0) return;
    const srcBoard = boards[srcIdx];
    const dstBoard = boards[dstIdx];
    const srcCards = [...srcBoard.cards];
    const [moved] = srcCards.splice(source.index, 1);
    moved.status = dstBoard.title;
    const dstCards = [...dstBoard.cards];
    dstCards.splice(destination.index, 0, moved);
    const updated = [...boards];
    updated[srcIdx] = { ...srcBoard, cards: srcCards };
    updated[dstIdx] = { ...dstBoard, cards: dstCards };
    setBoards(updated);
    axios.post("http://localhost:3001/api/updateTask", {
      id: moved.id,
      status: moved.status,
    });
  };

  return (
    <div className={`app ${darkTheme ? "dark" : ""}`}>
      <div className="app_nav">
        <h1>Kanban Board</h1>
        <label className="switch">
          <input
            type="checkbox"
            checked={darkTheme}
            onChange={() => setDarkTheme((d) => !d)}
          />
          <span className="slider round" />
        </label>
      </div>

      <div className="board-menu">
        <div className="menu-left">
          <div className="menu-item" onClick={handleSortClick}>
            Sort {sortValue !== "none" && `(${sortLabels[sortValue]})`}
            {showSortMenu && (
              <div className="dropdown-menu">
                {["none", "titleAsc", "titleDesc", "dateAsc", "dateDesc"].map((opt) => (
                  <div
                    key={opt}
                    className="dropdown-item"
                    onClick={() => setSort(opt)}
                  >
                    {opt === "none" ? "None" : sortLabels[opt]}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="menu-item" onClick={handleFilterClick}>
            Filter {filterStatus !== "all" && `(${filterStatus})`}
            {showFilterMenu && (
              <div className="dropdown-menu">
                {["all", ...statuses].map((opt) => (
                  <div
                    key={opt}
                    className="dropdown-item"
                    onClick={() => setFilter(opt)}
                  >
                    {opt}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
        <div className="menu-right">
          <div className="search-container">
            <svg
              className="search-icon"
              width="16"
              height="16"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              viewBox="0 0 24 24"
              aria-hidden="true"
            >
              <circle cx="11" cy="11" r="8" />
              <line x1="21" y1="21" x2="16.65" y2="16.65" />
            </svg>
            <input
              type="text"
              placeholder="Search..."
              className="search-no-border"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="app_boards_container">
          <div className="app_boards">
            {displayedBoards.map((board) => (
              <Board
                key={board.id}
                board={board}
                addCard={addCardHandler}
                updateCard={updateCard}
                removeCard={removeCard}
                darkTheme={darkTheme}
              />
            ))}
          </div>
        </div>
      </DragDropContext>
    </div>
  );
}
