import React, { useState } from "react";
import { Draggable } from "react-beautiful-dnd";
import { MoreHorizontal, Clock, CheckSquare } from "react-feather";

import Dropdown from "../Dropdown/Dropdown";
import CardInfo from "./CardInfo/CardInfo";
import "./Card.css";

function Card(props) {
  const [showDropdown, setShowDropdown] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const { id, title, labels, date, tasks } = props.card;

  // Подсчёт задач (для прогресса)
  const tasksCount = tasks?.length || 0;
  const tasksCompleted = tasks?.filter((t) => t.completed).length || 0;
  const progress = tasksCount > 0 
    ? Math.round((tasksCompleted / tasksCount) * 100)
    : 0;

  // Динамическое определение цвета прогресс-бара
  let progressColor = "#e74c3c"; // красный < 33%
  if (progress >= 33 && progress < 100) {
    progressColor = "#f1c40f"; // жёлтый
  } else if (progress === 100 && tasksCount > 0) {
    progressColor = "#2ecc71"; // зелёный
  }

  // Формат даты (пример)
  const formatDate = (value) => {
    if (!value) return "";
    const dateObj = new Date(value);
    if (isNaN(dateObj)) return value;
    const options = { day: "numeric", month: "short", year: "numeric" };
    return dateObj.toLocaleDateString("en-GB", options);
  };

  return (
    <>
      {showModal && (
        <CardInfo
          onClose={() => setShowModal(false)}
          card={props.card}
          boardId={props.boardId}
          updateCard={props.updateCard}
        />
      )}

      <Draggable draggableId={id} index={props.index}>
        {(provided, snapshot) => (
          <div
            className={`card ${snapshot.isDragging ? "dragging" : ""}`}
            ref={provided.innerRef}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            onClick={() => setShowModal(true)}
          >
            {/* Шапка: лейблы + кнопка «...». */}
            <div className="card_header">
              <div className="card_header_labels">
                {labels?.map((label, idx) => (
                  <span
                    key={idx}
                    className="card_label"
                    style={{ backgroundColor: label.color }}
                  >
                    {label.text}
                  </span>
                ))}
              </div>
              <div
                className="card_more"
                onClick={(e) => {
                  e.stopPropagation();
                  setShowDropdown(true);
                }}
              >
                <MoreHorizontal />
                {showDropdown && (
                  <Dropdown
                    class="board_dropdown"
                    onClose={() => setShowDropdown(false)}
                  >
                    <p onClick={() => props.removeCard(props.boardId, id)}>
                      Delete Card
                    </p>
                  </Dropdown>
                )}
              </div>
            </div>

            {/* Заголовок (title). Даже если он один, показываем его */}
            <div className="card_title">
              {title || "No Title"}
            </div>

            {/* Дата (если есть) */}
            {date && (
              <div className="card_due-date">
                <Clock size={14} style={{ verticalAlign: "middle", marginRight: 4 }} />
                {formatDate(date)}
              </div>
            )}

            {/* Прогресс-бар (показываем всегда).
                Если 0 задач, то пишем "0/0", сам бар = 0%. 
            */}
            <div className="card_progress">
              <div className="card_progress_text">
                <CheckSquare
                  size={14}
                  style={{ verticalAlign: "middle", marginRight: 4 }}
                />
                {tasksCompleted}/{tasksCount}
              </div>
              <div className="card_progress_bar">
                <div
                  className="card_progress_fill"
                  style={{
                    width: `${progress}%`,
                    backgroundColor: progressColor,
                  }}
                />
              </div>
            </div>
          </div>
        )}
      </Draggable>
    </>
  );
}

export default Card;
