// src/App.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  addTaskToSheet,
  updateTaskInSheet,
  deleteTaskFromSheet
} from "./googleSheet";
import { DragDropContext } from "react-beautiful-dnd";
import { Sun, Moon } from "lucide-react";
import Board from "./Components/Board/Board";
import "./App.css";

export default function App() {
  const [boards, setBoards] = useState([]);
  const [darkTheme, setDarkTheme] = useState(
    localStorage.getItem("kanban-theme") === "dark"
  );

  // фильтр/сорт/поиск
  const [filterStatus, setFilterStatus] = useState("all");
  const [sortValue, setSortValue] = useState("none");
  const [searchQuery, setSearchQuery] = useState("");
  const [showSortMenu, setShowSortMenu] = useState(false);
  const [showFilterMenu, setShowFilterMenu] = useState(false);

  const statuses = ["Очередь", "В работе", "На проверке", "Готово"];
  const sortLabels = {
    titleAsc: "Title A→Z",
    titleDesc: "Title Z→A",
    dateAsc: "Date Asc",
    dateDesc: "Date Desc",
  };

  // 1) Начальная загрузка: localStorage или API
  useEffect(() => {
    const saved = localStorage.getItem("kanban-boards");
    if (saved) {
      setBoards(JSON.parse(saved));
    } else {
      axios
        .get("/api/board")
        .then((res) => {
          const tasks = res.data.tasks || [];
          const grouped = statuses.map((st, i) => ({
            id: `${i + 1}`,
            title: st,
            cards: tasks.filter((t) => t.status === st),
          }));
          setBoards(grouped);
        })
        .catch((err) => console.error("Ошибка загрузки:", err));
    }
  }, []);

  // 2) Сохраняем тему
  useEffect(() => {
    localStorage.setItem("kanban-theme", darkTheme ? "dark" : "light");
  }, [darkTheme]);

  // 3) Сохраняем доски в localStorage при любых изменениях
  useEffect(() => {
    localStorage.setItem("kanban-boards", JSON.stringify(boards));
  }, [boards]);

  // фильтрация / поиск / сортировка
  const applyFilterSearchSort = (cards) => {
    let result = [...cards];
    if (filterStatus !== "all") {
      result = result.filter((c) => c.status === filterStatus);
    }
    if (searchQuery.trim()) {
      const q = searchQuery.toLowerCase();
      result = result.filter((c) =>
        c.title.toLowerCase().includes(q)
      );
    }
    if (sortValue === "titleAsc") {
      result.sort((a, b) => (a.title > b.title ? 1 : -1));
    } else if (sortValue === "titleDesc") {
      result.sort((a, b) => (a.title < b.title ? 1 : -1));
    } else if (sortValue === "dateAsc") {
      result.sort((a, b) => {
        if (!a.date) return 1;
        if (!b.date) return -1;
        return new Date(a.date) - new Date(b.date);
      });
    } else if (sortValue === "dateDesc") {
      result.sort((a, b) => {
        if (!a.date) return 1;
        if (!b.date) return -1;
        return new Date(b.date) - new Date(a.date);
      });
    }
    return result;
  };

  const displayedBoards = boards.map((b) => ({
    ...b,
    cards: applyFilterSearchSort(b.cards),
  }));

  // меню сортировки / фильтра
  const handleSortClick = () => {
    setShowSortMenu((v) => !v);
    setShowFilterMenu(false);
  };
  const handleFilterClick = () => {
    setShowFilterMenu((v) => !v);
    setShowSortMenu(false);
  };
  const setSort = (val) => {
    setSortValue(val);
    setShowSortMenu(false);
  };
  const setFilter = (val) => {
    setFilterStatus(val);
    setShowFilterMenu(false);
  };

  // CRUD карточек
  const addCardHandler = (boardId, cardData) => {
    setBoards((bs) =>
      bs.map((b) =>
        b.id === boardId
          ? { ...b, cards: [...b.cards, { ...cardData, status: b.title }] }
          : b
      )
    );
    axios.post("/api/addTask", { card: cardData }).catch(console.error);
    + addTaskToSheet({
   id: cardData.id,
   title: cardData.title,
   description: cardData.description,
   status: cardData.status,
   startDate: cardData.startDate,
   dueDate: cardData.dueDate,
   priority: cardData.priority,
   labels: cardData.labels
 }).catch(console.error);
  };

  const updateCard = (boardId, cardId, updatedCard) => {
    setBoards((bs) =>
      bs.map((b) =>
        b.id === boardId
          ? {
              ...b,
              cards: b.cards.map((c) =>
                c.id === cardId ? updatedCard : c
              ),
            }
          : b
      )
    );
    axios.post("/api/editTask", { card: updatedCard }).catch(console.error);
     updateTaskInSheet({
   id:          updatedCard.id,
   title:       updatedCard.title,
   description: updatedCard.description,
   status:      updatedCard.status,
   startDate:   updatedCard.startDate,
   dueDate:     updatedCard.dueDate,
   priority:    updatedCard.priority,
   labels:      updatedCard.labels
 }).catch(console.error);
  };

  const removeCard = (boardId, cardId) => {
    setBoards((bs) =>
      bs.map((b) =>
        b.id === boardId
          ? { ...b, cards: b.cards.filter((c) => c.id !== cardId) }
          : b
      )
    );
    axios.post("/api/deleteTask", { id: cardId }).catch(console.error);
  };

  // Drag & Drop
  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }
    setBoards((prev) => {
      const copy = prev.map((b) => ({
        ...b,
        cards: Array.from(b.cards),
      }));
      const srcBoard = copy.find((b) => b.id === source.droppableId);
      const dstBoard = copy.find((b) => b.id === destination.droppableId);
      const [moved] = srcBoard.cards.splice(source.index, 1);
      moved.status = dstBoard.title;
      dstBoard.cards.splice(destination.index, 0, moved);
      return copy;
    });
    axios
      .post("/api/updateTask", {
        id: result.draggableId,
        status: destination.droppableId,
      })
      .catch(console.error);
  };

  return (
    <div className={`app ${darkTheme ? "dark" : ""}`}>
      <div className="app_nav">
        <h1>Kanban Board</h1>
        <div
          className="theme-toggle"
          onClick={() => setDarkTheme((d) => !d)}
        >
          {darkTheme ? <Sun size={20} /> : <Moon size={20} />}
        </div>
      </div>

      <div className="board-menu">
        <div className="menu-left">
          <div className="menu-item" onClick={handleSortClick}>
            Sort {sortValue !== "none" && `(${sortLabels[sortValue]})`}
            {showSortMenu && (
              <div className="dropdown-menu">
                {[
                  "none",
                  "titleAsc",
                  "titleDesc",
                  "dateAsc",
                  "dateDesc",
                ].map((opt) => (
                  <div
                    key={opt}
                    className="dropdown-item"
                    onClick={() => setSort(opt)}
                  >
                    {opt === "none" ? "None" : sortLabels[opt]}
                  </div>
                ))}
              </div>
            )}
          </div>
          <div className="menu-item" onClick={handleFilterClick}>
            Filter {filterStatus !== "all" && `(${filterStatus})`}
            {showFilterMenu && (
              <div className="dropdown-menu">
                {["all", ...statuses].map((opt) => (
                  <div
                    key={opt}
                    className="dropdown-item"
                    onClick={() => setFilter(opt)}
                  >
                    {opt}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
        <div className="menu-right">
          <div className="search-container">
            <svg
              className="search-icon"
              width="16"
              height="16"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              viewBox="0 0 24 24"
              aria-hidden="true"
            >
              <circle cx="11" cy="11" r="8" />
              <line x1="21" y1="21" x2="16.65" y2="16.65" />
            </svg>
            <input
              type="text"
              placeholder="Search..."
              className="search-no-border"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="app_boards_container">
          <div className="app_boards">
            {displayedBoards.map((board) => (
              <Board
                key={board.id}
                board={board}
                addCard={addCardHandler}
                updateCard={updateCard}
                removeCard={removeCard}
                darkTheme={darkTheme}
              />
            ))}
          </div>
        </div>
      </DragDropContext>
    </div>
  );
}
