import React, { useEffect, useState } from "react";
import debounce from "lodash/debounce";
import { X } from "react-feather";
import { SquarePen, Plus, Trash, Clock } from "lucide-react";

import Modal from "../../Modal/Modal";
import Editable from "../../Editabled/Editable";
import "./CardInfo.css";

function CardInfo(props) {
  const { id, title, description, date, labels, tasks, status, startDate: initStart } = props.card;

  const [localValues, setLocalValues] = useState({
    title: title || "",
    description: description || "",
    status: status || "На проверке",
    startDate: initStart || new Date().toISOString().split("T")[0],
    dueDate: date || "",
    labels: labels || [],
    tasks: tasks || [],
  });

  // Синхронное сохранение с дебаунсом
  useEffect(() => {
    const debounced = debounce(() => {
      props.updateCard(props.boardId, id, {
        ...props.card,
        ...localValues,
      });
    }, 500);
    debounced();
    return () => {
      debounced.flush();
      debounced.cancel();
    };
  }, [localValues]);

  // Обновление полей
  const updateField = (field) => (value) =>
    setLocalValues((prev) => ({ ...prev, [field]: value }));

  // Методы для тегов
  const [newLabelText, setNewLabelText] = useState("");
  const [selectedColor, setSelectedColor] = useState("#fccb00");
  const addLabel = () => {
    const text = newLabelText.trim();
    if (!text) return;
    setLocalValues((prev) => ({
      ...prev,
      labels: [...prev.labels, { text, color: selectedColor }],
    }));
    setNewLabelText("");
  };
  const removeLabel = (index) => {
    setLocalValues((prev) => ({
      ...prev,
      labels: prev.labels.filter((_, i) => i !== index),
    }));
  };

  // Методы для чеклиста
  const addTask = (text) => {
    const t = text.trim();
    if (!t) return;
    setLocalValues((prev) => ({
      ...prev,
      tasks: [...prev.tasks, { id: Date.now(), text: t, completed: false }],
    }));
  };
  const toggleTask = (taskId) =>
    setLocalValues((prev) => ({
      ...prev,
      tasks: prev.tasks.map((t) =>
        t.id === taskId ? { ...t, completed: !t.completed } : t
      ),
    }));
  const removeTask = (taskId) =>
    setLocalValues((prev) => ({
      ...prev,
      tasks: prev.tasks.filter((t) => t.id !== taskId),
    }));

  const tasksCount = localValues.tasks.length;
  const tasksCompleted = localValues.tasks.filter((t) => t.completed).length;
  const progressPercent = tasksCount ? Math.round((tasksCompleted / tasksCount) * 100) : 0;

  // Вычисление динамического приоритета по дате
  const computePriority = () => {
    if (!localValues.dueDate) {
      return { label: "Не задано", icon: null, style: { backgroundColor: "var(--secondary-background)", color: "var(--text-muted)" } };
    }
    const start = new Date(localValues.startDate);
    const due = new Date(localValues.dueDate);
    const now = new Date();
    const total = due - start;
    const left = due - now;
    const ratio = left / total;
    if (ratio > 0.5) {
      return { label: "Несрочно", icon: null, style: { backgroundColor: "var(--green-300)", color: "var(--green-700)" } };
    } else if (ratio > 0.25) {
      return { label: "Поторопитесь", icon: null, style: { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" } };
    } else {
      return { label: "Срочно", icon: <Clock size={14} />, style: { backgroundColor: "var(--red-300)", color: "var(--red-700)" } };
    }
  };
  const { label: priorityLabel, icon: priorityIcon, style: priorityStyle } = computePriority();

  // Статические стили статуса
  const getStatusStyles = (st) => {
    switch (st) {
      case "Очередь": return { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" };
      case "В работе": return { backgroundColor: "var(--blue-300)", color: "var(--blue-700)" };
      case "На проверке": return { backgroundColor: "var(--red-300)", color: "var(--red-700)" };
      case "Готово": return { backgroundColor: "var(--green-300)", color: "var(--green-700)" };
      default: return { backgroundColor: "var(--secondary-background)", color: "var(--text-muted)" };
    }
  };

  return (
    <Modal onClose={props.onClose}>
      <div className="cardinfo_container">
        <div className="cardinfo_close-btn" onClick={props.onClose}>
          <X size={24} />
        </div>

        {/* Заголовок */}
        <h2 className="cardinfo_title">
          <Editable
            defaultValue={localValues.title}
            placeholder="Введите заголовок..."
            onSubmit={updateField("title")}
          />
        </h2>

        {/* Описание */}
        <div className="cardinfo_description">
          <Editable
            defaultValue={localValues.description}
            placeholder="Введите описание..."
            onSubmit={updateField("description")}
          />
        </div>

        {/* Табличные поля */}
        <div className="cardinfo_table-fields">
          <div className="row">
            <div className="row_label">Статус</div>
            <div className="row_value cardinfo_static-value" style={getStatusStyles(localValues.status)}>
              {localValues.status}
            </div>
          </div>

          <div className="row">
            <div className="row_label">Дата начала</div>
            <div className="row_value date-picker">
              <input
                type="date"
                value={localValues.startDate}
                onChange={(e) => updateField("startDate")(e.target.value)}
              />
            </div>
          </div>

          <div className="row">
            <div className="row_label">Дата завершения</div>
            <div className="row_value date-picker">
              <input
                type="date"
                value={localValues.dueDate}
                onChange={(e) => updateField("dueDate")(e.target.value)}
              />
            </div>
          </div>

          <div className="row">
            <div className="row_label">Приоритет</div>
            <div className="row_value cardinfo_static-value" style={priorityStyle}>
              {priorityIcon}
              {priorityLabel}
            </div>
          </div>

          <div className="row row-tags">
            <div className="row_label">Теги</div>
            <div className="row_value cardinfo_tags">
              <div className="cardinfo_label-input">
                <SquarePen className="editable_icon" />
                <input
                  className="editable_input"
                  type="text"
                  placeholder="Введите название тега"
                  value={newLabelText}
                  onChange={(e) => setNewLabelText(e.target.value)}
                />
                <div className="cardinfo_label-colors">
                  {["yellow","blue","green","red"].map((color, idx) => (
                    <div
                      key={idx}
                      className="cardinfo_color-circle"
                      style={{ backgroundColor: `var(--${color}-300)` }}
                      onClick={() => setSelectedColor(color)}
                    />
                  ))}
                </div>
                <button onClick={addLabel}>
                  <Plus />
                </button>
              </div>
              <div className="cardinfo_labels-list">
                {localValues.labels.map((lbl, i) => (
                  <div
                    key={i}
                    className="cardinfo_label-pill"
                    style={{ backgroundColor: `var(--${lbl.color}-300)`, color: `var(--${lbl.color}-700)` }}
                  >
                    {lbl.text}
                    <Trash className="cardinfo_label-remove" size={14} onClick={() => removeLabel(i)} />
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Вкладки слева */}
        <div className="cardinfo_tabs">
          <span className="cardinfo_tab-active">Чеклист</span>
          <span className="cardinfo_tab">Файлы</span>
          <span className="cardinfo_tab">Комментарии</span>
          <span className="cardinfo_tab">Активность</span>
        </div>

        {/* Чеклист */}
        <div className="cardinfo_checklist-header">
          <h3>Чеклист ({tasksCompleted}/{tasksCount})</h3>
        </div>
        <div className="cardinfo_progress-bar">
          <div className="cardinfo_progress-fill" style={{ width: `${progressPercent}%`, backgroundColor }} />
        </div>
        <div className="cardinfo_tasks">
          {localValues.tasks.map((task) => (
            <div key={task.id} className="cardinfo_task-item">
              <label className="custom-checkbox">
                <input type="checkbox" checked={task.completed} onChange={() => toggleTask(task.id)} />
                <span className="checkmark"></span>
              </label>
              <span>{task.text}</span>
              <Trash className="cardinfo_task-remove" size={14} onClick={() => removeTask(task.id)} />
            </div>
          ))}
        </div>
        <Editable defaultValue="Добавить задачу" placeholder="Введите задачу..." onSubmit={addTask} />
      </div>
    </Modal>
  );
}

export default CardInfo;
