import React, { useState } from "react";
import { Droppable } from "react-beautiful-dnd";
import { Plus } from "lucide-react";
import Card from "../Card/Card";
import "./Board.css";

function Board(props) {
  const [showAddCardForm, setShowAddCardForm] = useState(false);
  const [newCardTitle, setNewCardTitle] = useState("");
  const [newCardDescription, setNewCardDescription] = useState("");

  // Пастельные цвета для заголовков досок (настройте по своему вкусу)
  const boardHeaderColors = {
    Очередь: "#fae9a6",
    "В работе": "#c4e2ff",
    "На проверке": "#ffd0d0",
    Готово: "#b5f8b8",
  };

  // Выбираем цвет для текущей доски
  const headerColor = boardHeaderColors[props.board.title] || "#fdfdfd";

  const handleAddCard = () => {
    if (!newCardTitle.trim()) return;
    props.addCard(props.board.id, {
      title: newCardTitle.trim(),
      description: newCardDescription.trim(),
    });
    setNewCardTitle("");
    setNewCardDescription("");
    setShowAddCardForm(false);
  };

  return (
    <div className={`board ${props.darkTheme ? "dark" : ""}`}>
      <div className="board_header" style={{ backgroundColor: headerColor }}>
        <p className="board_header_title">
          <svg
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M6 14.5L12 8.5L18 14.5L6 14.5Z"
              fill="currentColor"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinejoin="round"
            />
          </svg>
          {props.board.title} <span>{props.board.cards.length}</span>
        </p>
      </div>

      <div className="board_body">
        {!showAddCardForm && (
          <div
            className="board_add-card-btn"
            onClick={() => setShowAddCardForm(true)}
          >
            <Plus size={20} />
            <span>Add card</span>
          </div>
        )}
        {showAddCardForm && (
          <div className="board_add-card-form">
            <input
              type="text"
              placeholder="Enter Card Title"
              value={newCardTitle}
              onChange={(e) => setNewCardTitle(e.target.value)}
            />
            <textarea
              rows="3"
              placeholder="Enter Card Description"
              value={newCardDescription}
              onChange={(e) => setNewCardDescription(e.target.value)}
            />
            <div className="board_add-card-form_actions">
              <button className="primary-btn" onClick={handleAddCard}>
                Add
              </button>
              <button
                className="close-btn"
                onClick={() => setShowAddCardForm(false)}
              >
                ✕
              </button>
            </div>
          </div>
        )}

        <Droppable droppableId={props.board.id}>
          {(provided, snapshot) => (
            <div
              className={`board_cards ${
                snapshot.isDraggingOver ? "dragover" : ""
              }`}
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              {props.board.cards.map((card, index) => (
                <Card
                  key={card.id}
                  card={card}
                  index={index}
                  boardId={props.board.id}
                  removeCard={props.removeCard}
                  updateCard={props.updateCard}
                  darkTheme={props.darkTheme}
                />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </div>
    </div>
  );
}

export default Board;
