import React, { useEffect, useState } from "react";
import axios from "axios";
import { DragDropContext } from "react-beautiful-dnd";
import Board from "./Components/Board/Board";
import "./App.css";

export default function App() {
  const [boards, setBoards] = useState([]);
  const [darkTheme, setDarkTheme] = useState(
    localStorage.getItem("kanban-theme") === "dark"
  );

  // Состояния для меню: фильтр, сортировка и поиск
  const [filterStatus, setFilterStatus] = useState("all");
  const [sortValue, setSortValue] = useState("none");
  const [searchQuery, setSearchQuery] = useState("");

  // Состояния для показа выпадающих списков
  const [showSortMenu, setShowSortMenu] = useState(false);
  const [showFilterMenu, setShowFilterMenu] = useState(false);

  // Возможные статусы (названия колонок)
  const statuses = ["Очередь", "В работе", "На проверке", "Готово"];

  // Маппинг для отображения сортировки (только для показа в скобках)
  const sortLabels = {
    titleAsc: "Title A→Z",
    titleDesc: "Title Z→A",
    dateAsc: "Date Asc",
    dateDesc: "Date Desc",
  };

  // Функция загрузки данных с backend (Google Sheets)
  const fetchBoardData = () => {
    axios
      .get("http://localhost:3001/api/board")
      .then((res) => {
        const tasks = res.data.tasks || [];
        const grouped = statuses.map((status, index) => ({
          id: `${index + 1}`,
          title: status,
          cards: tasks.filter((task) => task.status === status),
        }));
        setBoards(grouped);
      })
      .catch((err) => console.error("Ошибка загрузки:", err));
  };

  useEffect(() => {
    fetchBoardData();
  }, []);

  useEffect(() => {
    localStorage.setItem("kanban-theme", darkTheme ? "dark" : "light");
  }, [darkTheme]);

  // Глобальный обработчик кликов для закрытия выпадающих списков
  useEffect(() => {
    const handleClickOutside = (event) => {
      // Если клик не произошёл внутри элемента с классами dropdown-menu или menu-item,
      // закрываем оба выпадающих меню.
      if (
        !event.target.closest(".menu-item") &&
        !event.target.closest(".dropdown-menu")
      ) {
        setShowSortMenu(false);
        setShowFilterMenu(false);
      }
    };

    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, []);

  // Применяем фильтрацию, поиск и сортировку
  const applyFilterSearchSort = (cards) => {
    let result = [...cards];

    // Фильтр
    if (filterStatus !== "all") {
      result = result.filter((card) => card.status === filterStatus);
    }

    // Поиск
    if (searchQuery.trim() !== "") {
      const q = searchQuery.toLowerCase();
      result = result.filter((card) =>
        card.title.toLowerCase().includes(q)
      );
    }

    // Сортировка
    if (sortValue === "titleAsc") {
      result.sort((a, b) => (a.title > b.title ? 1 : -1));
    } else if (sortValue === "titleDesc") {
      result.sort((a, b) => (a.title < b.title ? 1 : -1));
    } else if (sortValue === "dateAsc") {
      result.sort((a, b) => {
        if (!a.date) return 1;
        if (!b.date) return -1;
        return new Date(a.date) - new Date(b.date);
      });
    } else if (sortValue === "dateDesc") {
      result.sort((a, b) => {
        if (!a.date) return 1;
        if (!b.date) return -1;
        return new Date(b.date) - new Date(a.date);
      });
    }
    return result;
  };

  // Генерируем доски с применёнными фильтрами/сортировкой/поиском
  const getDisplayedBoards = () => {
    return boards.map((board) => ({
      ...board,
      cards: applyFilterSearchSort(board.cards),
    }));
  };

  const displayedBoards = getDisplayedBoards();

  // Обработчики для меню dropdown
  const handleSortClick = () => {
    setShowSortMenu(!showSortMenu);
    setShowFilterMenu(false);
  };

  const handleFilterClick = () => {
    setShowFilterMenu(!showFilterMenu);
    setShowSortMenu(false);
  };

  const setSort = (value) => {
    setSortValue(value);
    setShowSortMenu(false);
  };

  const setFilter = (value) => {
    setFilterStatus(value);
    setShowFilterMenu(false);
  };

  // Добавление карточки
  const addCardHandler = (boardId, cardData) => {
    const board = boards.find((b) => b.id === boardId);
    if (!board) return;

    const newCard = {
      id: `${Date.now()}-${Math.random()}`,
      title: cardData.title,
      description: cardData.description,
      status: board.title,
      labels: [],
      date: "",
      tasks: [],
    };

    const updatedBoards = boards.map((b) =>
      b.id === boardId ? { ...b, cards: [...b.cards, newCard] } : b
    );
    setBoards(updatedBoards);
    axios.post("http://localhost:3001/api/addTask", { card: newCard });
  };

  // Обновление карточки
  const updateCard = (boardId, cardId, updatedCard) => {
    const updatedBoards = boards.map((board) =>
      board.id === boardId
        ? {
            ...board,
            cards: board.cards.map((c) =>
              c.id === cardId ? updatedCard : c
            ),
          }
        : board
    );
    setBoards(updatedBoards);
    axios.post("http://localhost:3001/api/editTask", { card: updatedCard })
      .catch((err) => console.error("Ошибка обновления карточки:", err));
  };

  // Удаление карточки
  const removeCard = (boardId, cardId) => {
    const updatedBoards = boards.map((board) =>
      board.id === boardId
        ? { ...board, cards: board.cards.filter((c) => c.id !== cardId) }
        : board
    );
    setBoards(updatedBoards);
    // Если нужно, добавить запрос для удаления с сервера.
  };

  // Drag & Drop
  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;
    const sourceIndex = boards.findIndex((b) => b.id === source.droppableId);
    const destIndex = boards.findIndex((b) => b.id === destination.droppableId);
    if (sourceIndex === -1 || destIndex === -1) return;

    const sourceBoard = boards[sourceIndex];
    const destBoard = boards[destIndex];
    const sourceCards = Array.from(sourceBoard.cards);
    const [movedCard] = sourceCards.splice(source.index, 1);
    movedCard.status = destBoard.title;
    const destCards = Array.from(destBoard.cards);
    destCards.splice(destination.index, 0, movedCard);

    const updatedBoards = [...boards];
    updatedBoards[sourceIndex] = { ...sourceBoard, cards: sourceCards };
    updatedBoards[destIndex] = { ...destBoard, cards: destCards };

    setBoards(updatedBoards);
    axios.post("http://localhost:3001/api/updateTask", {
      id: movedCard.id,
      status: movedCard.status,
    });
  };

  return (
    <div className={`app ${darkTheme ? "dark" : ""}`}>
      {/* Верхняя навигация */}
      <div className="app_nav">
        <h1>Kanban Board</h1>
        <label className="switch">
          <input 
            type="checkbox" 
            checked={darkTheme} 
            onChange={() => setDarkTheme((prev) => !prev)} 
          />
          <span className="slider round"></span>
        </label>
      </div>

      {/* Меню над досками */}
      <div className="board-menu">
        <div className="menu-left">
          <div className="menu-item" onClick={handleSortClick}>
            Sort {sortValue !== "none" && `(${sortLabels[sortValue]})`}
            {showSortMenu && (
              <div className="dropdown-menu">
                <div className="dropdown-item" onClick={() => setSort("none")}>
                  None
                </div>
                <div className="dropdown-item" onClick={() => setSort("titleAsc")}>
                  Title A→Z
                </div>
                <div className="dropdown-item" onClick={() => setSort("titleDesc")}>
                  Title Z→A
                </div>
                <div className="dropdown-item" onClick={() => setSort("dateAsc")}>
                  Date Asc
                </div>
                <div className="dropdown-item" onClick={() => setSort("dateDesc")}>
                  Date Desc
                </div>
              </div>
            )}
          </div>
          <div className="menu-item" onClick={handleFilterClick}>
            Filter {filterStatus !== "all" && `(${filterStatus})`}
            {showFilterMenu && (
              <div className="dropdown-menu">
                <div className="dropdown-item" onClick={() => setFilter("all")}>
                  All
                </div>
                <div className="dropdown-item" onClick={() => setFilter("Очередь")}>
                  Очередь
                </div>
                <div className="dropdown-item" onClick={() => setFilter("В работе")}>
                  В работе
                </div>
                <div className="dropdown-item" onClick={() => setFilter("На проверке")}>
                  На проверке
                </div>
                <div className="dropdown-item" onClick={() => setFilter("Готово")}>
                  Готово
                </div>
              </div>
            )}
          </div>
        </div>
        <div className="menu-right">
          <div className="search-container">
            <svg
              className="search-icon"
              width="16"
              height="16"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              viewBox="0 0 24 24"
              aria-hidden="true"
            >
              <circle cx="11" cy="11" r="8"></circle>
              <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
            </svg>
            <input
              type="text"
              placeholder="Search..."
              className="search-no-border"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="app_boards_container">
          <div className="app_boards">
            {displayedBoards.map((board) => (
              <Board
                key={board.id}
                board={board}
                addCard={addCardHandler}
                removeCard={removeCard}
                updateCard={updateCard}
              />
            ))}
          </div>
        </div>
      </DragDropContext>
    </div>
  );
}
