// src/googleSheet.js

// Вставь сюда URL, который ты получил при деплое Web App (кончается на '/exec')
const SHEET_WEBAPP_URL = 
  "https://script.google.com/macros/s/AKfycbxTNHlIkAmpMUgnw7rtsB334BceQk9lFO4kKPWugRKIhYDZbtnv_OFZBnsTbYxe0q0U/exec";

/**
 * Общая функция для POST на Google Apps Script
 * @param {"add"|"update"|"delete"} action
 * @param {object} payload
 */
function postToSheet(action, payload) {
  const form = new URLSearchParams();
  form.append("action", action);
  form.append("contents", JSON.stringify(payload));

  return fetch(SHEET_WEBAPP_URL, {
    method: "POST",
    body: form
  }).then(res => {
    if (!res.ok) throw new Error(`Sheet API error ${res.status}`);
    return res.json();
  });
}

export function addTaskToSheet(task) {
  return postToSheet("add", task);
}

export function updateTaskInSheet(task) {
  return postToSheet("update", task);
}

export function deleteTaskFromSheet(id) {
  return postToSheet("delete", { id });
}
