// Board.jsx
import React, { useState } from "react";
import { Droppable } from "react-beautiful-dnd";
import { Plus } from "lucide-react";

import Modal from "../../Modal/Modal";
import "./Board.css";

function Board(props) {
  const [showAddModal, setShowAddModal] = useState(false);

  // единое состояние для всех полей новой карточки
  const [newCard, setNewCard] = useState({
    title: "",
    description: "",
    startDate: new Date().toISOString().split("T")[0], // сегодня
    dueDate: "",
    tasks: [],
    taskInput: "",
  });

  // добавить задачу в чек-лист
  const addTask = () => {
    const text = newCard.taskInput.trim();
    if (!text) return;
    setNewCard((c) => ({
      ...c,
      tasks: [...c.tasks, { id: Date.now(), text, completed: false }],
      taskInput: "",
    }));
  };

  const toggleTask = (id) => {
    setNewCard((c) => ({
      ...c,
      tasks: c.tasks.map((t) =>
        t.id === id ? { ...t, completed: !t.completed } : t
      ),
    }));
  };

  const removeTask = (id) => {
    setNewCard((c) => ({
      ...c,
      tasks: c.tasks.filter((t) => t.id !== id),
    }));
  };

  // по клику "Save" сохраняем новую карточку
  const handleSave = () => {
    if (!newCard.title.trim()) return;
    // пробрасываем все поля (startDate, dueDate, tasks и т.д.)
    props.addCard(props.board.id, {
      ...newCard,
      status: props.board.title,
      labels: [],
    });
    // сброс формы
    setNewCard({
      title: "",
      description: "",
      startDate: new Date().toISOString().split("T")[0],
      dueDate: "",
      tasks: [],
      taskInput: "",
    });
    setShowAddModal(false);
  };

  return (
    <div className={`board ${props.darkTheme ? "dark" : ""}`}>
      {/* ... ваш существующий header и Droppable ... */}

      <div className="board_body">
        <div
          className="board_add-card-btn"
          onClick={() => setShowAddModal(true)}
        >
          <Plus size={20} />
          <span>Add card</span>
        </div>

        {showAddModal && (
          <Modal onClose={() => setShowAddModal(false)}>
            <div className="add-card-modal">
              <h3>New Card</h3>
              <input
                type="text"
                placeholder="Title"
                value={newCard.title}
                onChange={(e) =>
                  setNewCard((c) => ({ ...c, title: e.target.value }))
                }
              />
              <textarea
                placeholder="Description"
                rows={3}
                value={newCard.description}
                onChange={(e) =>
                  setNewCard((c) => ({ ...c, description: e.target.value }))
                }
              />

              <label>
                Start Date:
                <input
                  type="date"
                  value={newCard.startDate}
                  readOnly
                />
              </label>

              <label>
                Due Date:
                <input
                  type="date"
                  value={newCard.dueDate}
                  onChange={(e) =>
                    setNewCard((c) => ({ ...c, dueDate: e.target.value }))
                  }
                />
              </label>

              <div className="tasks-section">
                <h4>Tasks</h4>
                {newCard.tasks.map((t) => (
                  <div key={t.id} className="task-item">
                    <input
                      type="checkbox"
                      checked={t.completed}
                      onChange={() => toggleTask(t.id)}
                    />
                    <span className={t.completed ? "done" : ""}>
                      {t.text}
                    </span>
                    <button onClick={() => removeTask(t.id)}>×</button>
                  </div>
                ))}
                <div className="add-task">
                  <input
                    type="text"
                    placeholder="New task"
                    value={newCard.taskInput}
                    onChange={(e) =>
                      setNewCard((c) => ({ ...c, taskInput: e.target.value }))
                    }
                    onKeyDown={(e) => e.key === "Enter" && addTask()}
                  />
                  <button onClick={addTask}>Add</button>
                </div>
              </div>

              <div className="modal-actions">
                <button className="primary-btn" onClick={handleSave}>
                  Save
                </button>
                <button onClick={() => setShowAddModal(false)}>Cancel</button>
              </div>
            </div>
          </Modal>
        )}

        <Droppable droppableId={props.board.id}>
          {(provided, snapshot) => (
            <div
              className={`board_cards ${
                snapshot.isDraggingOver ? "dragover" : ""
              }`}
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              {props.board.cards.map((card, index) => (
                <Card
                  key={card.id}
                  card={card}
                  index={index}
                  boardId={props.board.id}
                  removeCard={props.removeCard}
                  updateCard={props.updateCard}
                  darkTheme={props.darkTheme}
                />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </div>
    </div>
  );
}

export default Board;
