import React, { useState, useRef, useEffect } from "react";
import { SquarePen } from "lucide-react";
import "./Editable.css";

function Editable({
  text = "",
  defaultValue = "",
  placeholder = "",
  onSubmit,
  shortLine = false,
}) {
  const [value, setValue] = useState(defaultValue || text || "");
  const [isEditing, setIsEditing] = useState(false);
  const containerRef = useRef(null);
  const inputRef = useRef(null);

  // Клик вне поля — завершаем редактирование
  useEffect(() => {
    function handleClickOutside(e) {
      if (
        isEditing &&
        containerRef.current &&
        !containerRef.current.contains(e.target)
      ) {
        finishEdit();
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [isEditing]);

  // Фокус и курсор в конец при старте редактирования
  useEffect(() => {
    if (isEditing && inputRef.current) {
      inputRef.current.focus();
      const len = inputRef.current.value.length;
      inputRef.current.setSelectionRange(len, len);
    }
  }, [isEditing]);

  const startEdit = (e) => {
    e.stopPropagation();
    setIsEditing(true);
  };

  const finishEdit = () => {
    setIsEditing(false);
    onSubmit && onSubmit(value.trim());
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      finishEdit();
    }
  };

  return (
    <div
      className={`editable_line ${shortLine ? "short" : ""}`}
      ref={containerRef}
      onClick={!isEditing ? startEdit : (e) => e.stopPropagation()}
    >
      <SquarePen className="editable_icon" />

      {!isEditing ? (
        <span
          className={`editable_text ${!value ? "placeholder" : ""}`}
        >
          {value || placeholder}
        </span>
      ) : (
        <input
          ref={inputRef}
          type="text"
          className={`editable_input ${shortLine ? "short" : ""}`}
          value={value}
          onChange={(e) => setValue(e.target.value)}
          onBlur={finishEdit}
          onKeyDown={handleKeyDown}
          placeholder={placeholder}
        />
      )}
    </div>
  );
}

export default Editable;
