import React, { useEffect, useState } from "react";
import debounce from "lodash/debounce";
import { X, Calendar, CheckSquare, Edit3 } from "react-feather";
import { SquarePen, Plus, Trash, Clock } from "lucide-react";

import Modal from "../../Modal/Modal";
import Editable from "../../Editabled/Editable";
import "./CardInfo.css";

function CardInfo(props) {
  // Деструктурируем поля из props.card
  const {
    id,
    title,
    description,
    status,
    labels: initLabels,
    tasks: initTasks,
    startDate: initStart,
    date: initDue,
  } = props.card;

  // Локальный стейт для всех полей карточки
  const [localValues, setLocalValues] = useState({
    title: title || "",
    description: description || "",
    status: status || "На проверке",
    startDate: initStart || new Date().toISOString().split("T")[0],
    dueDate: initDue || "",
    labels: initLabels || [],
    tasks: initTasks || [],
  });

  // Дебаунс сохранение изменений
  useEffect(() => {
    const debounced = debounce(() => {
      props.updateCard(props.boardId, id, { ...props.card, ...localValues });
    }, 500);
    debounced();
    return () => {
      debounced.flush();
      debounced.cancel();
    };
  }, [localValues]);

  // Утилита для обновления любого поля
  const updateField = (field) => (value) =>
    setLocalValues((prev) => ({ ...prev, [field]: value }));

  const updateTitle = updateField("title");
  const updateDescription = updateField("description");
  const updateStartDate = updateField("startDate");
  const updateDueDate = updateField("dueDate");

  // Методы для работы с тегами
  const [newLabelText, setNewLabelText] = useState("");
  const [selectedColor, setSelectedColor] = useState("yellow");
  const addLabel = () => {
    const text = newLabelText.trim();
    if (!text) return;
    setLocalValues((prev) => ({
      ...prev,
      labels: [...prev.labels, { text, color: selectedColor }],
    }));
    setNewLabelText("");
  };
  const removeLabel = (index) =>
    setLocalValues((prev) => ({
      ...prev,
      labels: prev.labels.filter((_, i) => i !== index),
    }));

  // Методы для чеклиста
  const addTask = (text) => {
    const t = text.trim();
    if (!t) return;
    setLocalValues((prev) => ({
      ...prev,
      tasks: [...prev.tasks, { id: Date.now(), text: t, completed: false }],
    }));
  };
  const toggleTask = (taskId) =>
    setLocalValues((prev) => ({
      ...prev,
      tasks: prev.tasks.map((t) =>
        t.id === taskId ? { ...t, completed: !t.completed } : t
      ),
    }));
  const removeTask = (taskId) =>
    setLocalValues((prev) => ({
      ...prev,
      tasks: prev.tasks.filter((t) => t.id !== taskId),
    }));

  // Прогресс выполнения задач
  const tasksCount = localValues.tasks.length;
  const tasksCompleted = localValues.tasks.filter((t) => t.completed).length;
  const progressPercent =
    tasksCount === 0 ? 0 : Math.round((tasksCompleted / tasksCount) * 100);
  let progressColor = "#e74c3c";
  if (progressPercent >= 33 && progressPercent < 100) progressColor = "#f1c40f";
  if (progressPercent === 100) progressColor = "#2ecc71";

  // Логика расчёта приоритета по дате
  const computePriority = () => {
    if (!localValues.dueDate) {
      return { label: "Не задано", style: { backgroundColor: "var(--secondary-background)", color: "var(--text-muted)" } };
    }
    const start = new Date(localValues.startDate);
    const due = new Date(localValues.dueDate);
    const now = new Date();
    const total = due - start;
    const left = due - now;
    const ratio = left / total;
    if (ratio > 0.5) return { label: "Несрочно", style: { backgroundColor: "var(--green-300)", color: "var(--green-700)" } };
    if (ratio > 0.25) return { label: "Поторопитесь", style: { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" } };
    return { label: "Срочно", style: { backgroundColor: "var(--red-300)", color: "var(--red-700)" } };
  };
  const { label: priorityLabel, style: priorityStyle } = computePriority();

  // Стили для статуса задачи
  const getStatusStyles = (st) => {
    switch (st) {
      case "Очередь": return { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" };
      case "В работе": return { backgroundColor: "var(--blue-300)", color: "var(--blue-700)" };
      case "На проверке": return { backgroundColor: "var(--red-300)", color: "var(--red-700)" };
      case "Готово": return { backgroundColor: "var(--green-300)", color: "var(--green-700)" };
      default: return { backgroundColor: "var(--secondary-background)", color: "var(--text-muted)" };
    }
  };

  return (
    <Modal onClose={props.onClose}>
      <div className="cardinfo_container">
        <div className="cardinfo_close-btn" onClick={props.onClose}><X size={24} /></div>

        {/* Заголовок */}
        <h2 className="cardinfo_title">
          <Editable defaultValue={localValues.title} placeholder="Введите заголовок..." onSubmit={updateTitle} />
        </h2>

        {/* Описание */}
        <div className="cardinfo_description">
          <Editable defaultValue={localValues.description} placeholder="Введите описание..." onSubmit={updateDescription} />
        </div>

        {/* Табличный вид */}
        <div className="cardinfo_table-fields">
          <div className="row">
            <div className="row_label">Статус</div>
            <div className="row_value cardinfo_static-value" style={getStatusStyles(localValues.status)}>
              {localValues.status}
            </div>
          </div>

          <div className="row">
            <div className="row_label">Дата начала</div>
            <div className="row_value date-picker">
              <Calendar size={16} />
              <input type="date" value={localValues.startDate} onChange={(e) => updateStartDate(e.target.value)} />
            </div>
          </div>

          <div className="row">
            <div className="row_label">Дата завершения</div>
            <div className="row_value date-picker">
              <Calendar size={16} />
              <input type="date" value={localValues.dueDate} onChange={(e) => updateDueDate(e.target.value)} />
            </div>
          </div>

          <div className="row">
            <div className="row_label">Приоритет</div>
            <div className="row_value cardinfo_static-value" style={priorityStyle}>
              {priorityLabel === "Срочно" && <Clock size={14} />} {priorityLabel}
            </div>
          </div>

          <div className="row row-tags">
            <div className="row_label">Теги</div>
            <div className="row_value cardinfo_tags">
              <div className="cardinfo_label-input">
                <SquarePen className="editable_icon" />
                <input className="editable_input" type="text" placeholder="Введите название тега" value={newLabelText} onChange={(e) => setNewLabelText(e.target.value)} />
                {['yellow','blue','green','red'].map((c) => (
                  <div key={c} className="cardinfo_color-circle" style={{ backgroundColor: `var(--${c}-300)` }} onClick={() => setSelectedColor(c)} />
                ))}
                <button type="button" onClick={addLabel}><Plus size={16}/> Добавить</button>
              </div>
              <div className="cardinfo_labels-list">
                {localValues.labels.map((lbl, idx) => (
                  <div key={idx} className="cardinfo_label-pill" style={{ backgroundColor: `var(--${lbl.color}-300)`, color: `var(--${lbl.color}-700)` }}>
                    {lbl.text} <Trash size={14} className="cardinfo_label-remove" onClick={() => removeLabel(idx)} />
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Вкладки */}
        <div className="cardinfo_tabs">
          <span className="cardinfo_tab-active">Чеклист</span>
          <span className="cardinfo_tab">Файлы</span>
          <span className="cardinfo_tab">Комментарии</span>
          <span className="cardinfo_tab">Активность</span>
        </div>

        {/* Чеклист */}
        <div className="cardinfo_checklist-header"><h3>Чеклист ({tasksCompleted}/{tasksCount})</h3></div>
        <div className="cardinfo_progress-bar"><div className="cardinfo_progress-fill" style={{ width: `${progressPercent}%`, backgroundColor: progressColor }} /></div>
        <div className="cardinfo_tasks">
          {localValues.tasks.map((task) => (
            <div key={task.id} className="cardinfo_task-item">
              <label className="custom-checkbox">
                <input type="checkbox" checked={task.completed} onChange={() => toggleTask(task.id)} />
                <span className="checkmark"></span>
              </label>
              <span>{task.text}</span>
              <Trash size={14} className="cardinfo_task-remove" onClick={() => removeTask(task.id)} />
            </div>
          ))}
        </div>
        <Editable defaultValue="Добавить задачу" placeholder="Введите задачу..." onSubmit={addTask} />
      </div>
    </Modal>
  );
}

export default CardInfo;
