import React, { useState, useRef, useEffect } from "react";
import { Edit2 } from "react-feather";
import { SquarePen } from "lucide-react";
import "./Editable.css";

function Editable({
  text = "",
  defaultValue = "",
  placeholder = "",
  onSubmit,
  shortLine = false,
}) {
  const [value, setValue] = useState(defaultValue || text || "");
  const [isEditing, setIsEditing] = useState(false);
  const containerRef = useRef(null);
  const inputRef = useRef(null);

  // При клике вне поля ввода => завершаем редактирование
  useEffect(() => {
    function handleClickOutside(e) {
      if (
        isEditing &&
        containerRef.current &&
        !containerRef.current.contains(e.target)
      ) {
        finishEdit();
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [isEditing]);

  // Ставим фокус и курсор в конец, когда переходим в режим редактирования
  useEffect(() => {
    if (isEditing && inputRef.current) {
      inputRef.current.focus();
      const len = inputRef.current.value.length;
      inputRef.current.setSelectionRange(len, len);
    }
  }, [isEditing]);

  const startEdit = (e) => {
    e.stopPropagation(); // чтобы не вызывался клик на родителе (модалка и т.д.)
    setIsEditing(true);
  };

  const finishEdit = () => {
    setIsEditing(false);
    if (onSubmit) {
      onSubmit(value.trim());
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      finishEdit();
    }
  };

  return (
    <div
      className={`editable_line ${shortLine ? "short" : ""}`}
      ref={containerRef}
      onClick={!isEditing ? startEdit : (e) => e.stopPropagation()}
    >
      {/* Карандаш всегда слева, никогда не исчезает */}
      <SquarePen className="editable_icon" />

      {/* Если не редактируем – показываем просто <span> */}
      {!isEditing && (
        <span className="editable_text">{value || placeholder}</span>
      )}

      {/* Если редактируем – показываем <input>, иконка остаётся слева */}
      {isEditing && (
        <input
          ref={inputRef}
          type="text"
          className={`editable_input ${shortLine ? "short" : ""}`}
          value={value}
          onChange={(e) => setValue(e.target.value)}
          onBlur={finishEdit}
          onKeyDown={handleKeyDown}
          placeholder={placeholder}
        />
      )}
    </div>
  );
}

export default Editable;
