// src/Components/CardInfo/CardInfo.js

import React, { useEffect, useState, useRef } from "react";
import debounce from "lodash/debounce";
import { X } from "react-feather";
import { SquarePen, Plus, Trash, Clock } from "lucide-react";

import Modal from "../../Modal/Modal";
import Editable from "../../Editabled/Editable";
import "./CardInfo.css";

function CardInfo(props) {
  const {
    id,
    title,
    description,
    date,        // исходное поле с датой завершения
    labels,
    tasks,
    status,
    startDate: initStart,
  } = props.card;

  const [localValues, setLocalValues] = useState({
    title: title || "",
    description: description || "",
    status: status || "На проверке",
    startDate: initStart || new Date().toISOString().split("T")[0],
    dueDate: date || "",
    priority: "Неизвестно",
    labels: labels || [],
    tasks: tasks || [],
  });

  // реф для <textarea>
  const descRef = useRef(null);

  // авто-размер textarea
  const adjustTextareaHeight = () => {
    const el = descRef.current;
    if (el) {
      el.style.height = "auto";
      el.style.height = el.scrollHeight + "px";
    }
  };

  useEffect(() => {
    adjustTextareaHeight();
  }, [localValues.description]);

  // сохранить изменения с дебаунсом
  useEffect(() => {
    const debounced = debounce(() => {
      props.updateCard(
        props.boardId,
        id,
        {
          ...props.card,
          ...localValues,
          date: localValues.dueDate,
        }
      );
    }, 500);
    debounced();
    return () => {
      debounced.flush();
      debounced.cancel();
    };
  }, [localValues]);

  // вычисление приоритета
  const computePriority = (start, due) => {
    if (!due) return "Неизвестно";
    const sd = new Date(start), dd = new Date(due);
    const diffDays = (dd - sd) / (1000 * 60 * 60 * 24);
    if (diffDays <= 3) return "Срочный";
    if (diffDays <= 7) return "Высокий";
    if (diffDays <= 14) return "Средний";
    return "Низкий";
  };

  useEffect(() => {
    const newPr = computePriority(localValues.startDate, localValues.dueDate);
    if (newPr !== localValues.priority) {
      setLocalValues(p => ({ ...p, priority: newPr }));
    }
  }, [localValues.startDate, localValues.dueDate]);

  // обновление полей
  const updateTitle = val =>
    setLocalValues(p => ({ ...p, title: val }));
  const updateDescription = val =>
    setLocalValues(p => ({ ...p, description: val }));
  const updateStartDate = val =>
    setLocalValues(p => ({ ...p, startDate: val }));
  const updateDueDate = val =>
    setLocalValues(p => ({ ...p, dueDate: val }));

  // теги
  const [newLabelText, setNewLabelText] = useState("");
  const [selectedColor, setSelectedColor] = useState("");
  const [labelError, setLabelError] = useState("");

  const addLabel = () => {
    if (!newLabelText.trim()) return;
    if (!selectedColor) {
      setLabelError("Выберите цвет тега");
      return;
    }
    const newLabel = { text: newLabelText.trim(), color: selectedColor };
    setLocalValues(p => ({
      ...p,
      labels: [...p.labels, newLabel],
    }));
    setNewLabelText("");
    setSelectedColor("");
    setLabelError("");
  };

  const removeLabel = index => {
    const updated = [...localValues.labels];
    updated.splice(index, 1);
    setLocalValues(p => ({ ...p, labels: updated }));
  };

  // чеклист
  const [taskKey, setTaskKey] = useState(0);

  const addTask = text => {
    const trimmed = text.trim();
    if (!trimmed) return;
    const newTask = { id: Date.now() + Math.random(), text: trimmed, completed: false };
    setLocalValues(p => ({
      ...p,
      tasks: [...p.tasks, newTask],
    }));
    setTaskKey(k => k + 1);
  };

  const toggleTask = taskId => {
    const updated = localValues.tasks.map(t =>
      t.id === taskId ? { ...t, completed: !t.completed } : t
    );
    setLocalValues(p => ({ ...p, tasks: updated }));
  };

  const removeTask = taskId => {
    const updated = localValues.tasks.filter(t => t.id !== taskId);
    setLocalValues(p => ({ ...p, tasks: updated }));
  };

  const tasksCount = localValues.tasks.length;
  const tasksCompleted = localValues.tasks.filter(t => t.completed).length;
  const progressPercent = tasksCount === 0 ? 0 : Math.round((tasksCompleted / tasksCount) * 100);

  let progressColor = "#e74c3c";
  if (progressPercent >= 33 && progressPercent < 100) progressColor = "#f1c40f";
  else if (progressPercent === 100) progressColor = "#2ecc71";

  const getStatusStyles = val => {
    switch (val) {
      case "Очередь":    return { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" };
      case "В работе":   return { backgroundColor: "var(--blue-300)",   color: "var(--blue-700)" };
      case "На проверке": case "Срочный":
                        return { backgroundColor: "var(--red-300)",    color: "var(--red-700)" };
      case "Высокий":    return { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" };
      case "Средний":    return { backgroundColor: "var(--blue-300)",   color: "var(--blue-700)" };
      case "Низкий":     return { backgroundColor: "var(--green-300)",  color: "var(--green-700)" };
      case "Неизвестно": return {
                           backgroundColor: "var(--secondary-background)",
                           color: "var(--text-muted)"
                         };
      case "Готово":     return { backgroundColor: "var(--green-300)",  color: "var(--green-700)" };
      default:           return {
                           backgroundColor: "var(--secondary-background)",
                           color: "var(--text-muted)"
                         };
    }
  };

  return (
    <Modal onClose={props.onClose}>
      <div className="cardinfo_container">
        <div className="cardinfo_close-btn" onClick={props.onClose}>
          <X size={24} />
        </div>

        {/* Заголовок */}
        <h2 className="cardinfo_title">
          <Editable
            defaultValue={localValues.title}
            placeholder="Введите заголовок..."
            onSubmit={updateTitle}
          />
        </h2>

        {/* Описание */}
        <div className="cardinfo_description">
          <textarea
            ref={descRef}
            className="cardinfo_description-text"
            value={localValues.description}
            placeholder="Введите описание..."
            onChange={e => updateDescription(e.target.value)}
          />
        </div>

        {/* Основные поля */}
        <div className="cardinfo_table-fields">
          {/* Статус */}
          <div className="row">
            <div className="row_label">Статус</div>
            <div
              className="row_value cardinfo_static-value"
              style={getStatusStyles(localValues.status)}
            >
              {localValues.status}
            </div>
          </div>

          {/* Даты */}
          <div className="row">
            <div className="row_label">Дата начала</div>
            <div className="row_value date-picker">
              <input
                type="date"
                value={localValues.startDate}
                onChange={e => updateStartDate(e.target.value)}
              />
            </div>
          </div>
          <div className="row">
            <div className="row_label">Дата завершения</div>
            <div className="row_value date-picker">
              <input
                type="date"
                value={localValues.dueDate}
                onChange={e => updateDueDate(e.target.value)}
              />
            </div>
          </div>

          {/* Приоритет */}
          <div className="row">
            <div className="row_label">Приоритет</div>
            <div
              className="row_value cardinfo_static-value"
              style={getStatusStyles(localValues.priority)}
            >
              {localValues.priority === "Срочный" && <Clock size={14} />}
              {localValues.priority}
            </div>
          </div>

          {/* Теги */}
          <div className="row row-tags">
            <div className="row_label">Теги</div>
            <div className="row_value cardinfo_tags">
              <div className="cardinfo_label-input">
                <SquarePen className="editable_icon" />
                <input
                  className="editable_input"
                  type="text"
                  placeholder="Введите тег"
                  value={newLabelText}
                  onChange={e => {
                    setNewLabelText(e.target.value);
                    setLabelError("");
                  }}
                />
                <div className="cardinfo_label-colors">
                  {["yellow","blue","green","red"].map(color => (
                    <div
                      key={color}
                      className={`cardinfo_color-circle${selectedColor===color?" selected":""}`}
                      style={{ backgroundColor: `var(--${color}-300)` }}
                      onClick={() => {
                        setSelectedColor(color);
                        setLabelError("");
                      }}
                    />
                  ))}
                </div>
                <button className="btn-add-label" onClick={addLabel}>
                  <Plus /><span>Добавить</span>
                </button>
              </div>
              {labelError && <div className="label-error">{labelError}</div>}
              <div className="cardinfo_labels-list">
                {localValues.labels.map((lab,i) => (
                  <div
                    key={i}
                    className="cardinfo_label-pill"
                    style={{
                      color: `var(--${lab.color}-700)`,
                      backgroundColor: `var(--${lab.color}-300)`
                    }}
                  >
                    <span>{lab.text}</span>
                    <Trash
                      className="cardinfo_label-remove"
                      size={14}
                      onClick={() => removeLabel(i)}
                    />
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Вкладки */}
        <div className="cardinfo_tabs">
          <span className="cardinfo_tab-active">Чеклист</span>
          <span className="cardinfo_tab">Файлы</span>
          <span className="cardinfo_tab">Комментарии</span>
          <span className="cardinfo_tab">Активность</span>
        </div>

        {/* Чеклист */}
        <div className="cardinfo_checklist-header">
          <h3>Чеклист ({tasksCompleted}/{tasksCount})</h3>
        </div>
        <div className="cardinfo_progress-bar">
          <div
            className="cardinfo_progress-fill"
            style={{
              width: `${progressPercent}%`,
              backgroundColor: progressColor
            }}
          />
        </div>
        <div className="cardinfo_tasks">
          {localValues.tasks.map(task => (
            <div key={task.id} className="cardinfo_task-item">
              <label className="custom-checkbox">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleTask(task.id)}
                />
                <span className="checkmark"></span>
              </label>
              <span>{task.text}</span>
              <Trash
                className="cardinfo_task-remove"
                size={14}
                onClick={() => removeTask(task.id)}
              />
            </div>
          ))}
        </div>

        {/* Добавить задачу */}
        <Editable
          key={taskKey}
          defaultValue=""
          placeholder="Добавить задачу"
          onSubmit={addTask}
        />
      </div>
    </Modal>
  );
}

export default CardInfo;
