import React, { useEffect, useState } from "react";
import debounce from "lodash/debounce";
import { X } from "react-feather";
import { Trash, Clock } from "lucide-react";

import Modal from "../../Modal/Modal";
import Editable from "../../Editabled/Editable";
import "./CardInfo.css";

function CardInfo(props) {
  const { id, title, description, startDate: initStart, date: initDue, status, labels, tasks } = props.card;

  const [localValues, setLocalValues] = useState({
    title: title || "",
    description: description || "",
    status: status || "На проверке",
    // синхронизируем дату начала с тем, что было при создании
    startDate: initStart || new Date().toISOString().split("T")[0],
    // дата завершения может быть пустой
    dueDate: initDue || "",
    labels: labels || [],
    tasks: tasks || [],
  });

  // Debounce-сохранение
  useEffect(() => {
    const debounced = debounce(() => {
      props.updateCard(props.boardId, id, {
        ...props.card,
        ...localValues,
      });
    }, 500);

    debounced();
    return () => {
      debounced.flush();
      debounced.cancel();
    };
  }, [localValues]);

  const updateField = (field) => (value) =>
    setLocalValues((prev) => ({ ...prev, [field]: value }));

  // Прогресс задач
  const tasksCount = localValues.tasks.length;
  const tasksCompleted = localValues.tasks.filter((t) => t.completed).length;
  const progressPercent = tasksCount ? Math.round((tasksCompleted / tasksCount) * 100) : 0;

  // Вычисление приоритета по оставшемуся времени
  let priorityLabel, priorityStyle;
  if (!localValues.dueDate) {
    priorityLabel = "Не задано";
    priorityStyle = {
      backgroundColor: "var(--secondary-background)",
      color: "var(--text-muted)"
    };
  } else {
    const start = new Date(localValues.startDate);
    const due = new Date(localValues.dueDate);
    const now = new Date();
    const total = due - start;
    const left = due - now;
    const ratio = left / total;
    if (ratio > 0.5) {
      priorityLabel = "Несрочно";
      priorityStyle = { backgroundColor: "var(--green-300)", color: "var(--green-700)" };
    } else if (ratio > 0.25) {
      priorityLabel = "Поторопитесь";
      priorityStyle = { backgroundColor: "var(--yellow-300)", color: "var(--yellow-700)" };
    } else {
      priorityLabel = "Срочно";
      priorityStyle = { backgroundColor: "var(--red-300)", color: "var(--red-700)" };
    }
  }

  // Управление чеклистом
  const addTask = (text) => {
    if (!text.trim()) return;
    setLocalValues((prev) => ({
      ...prev,
      tasks: [...prev.tasks, { id: Date.now(), text, completed: false }]
    }));
  };
  const toggleTask = (taskId) =>
    setLocalValues((prev) => ({
      ...prev,
      tasks: prev.tasks.map((t) => (t.id === taskId ? { ...t, completed: !t.completed } : t))
    }));
  const removeTask = (taskId) =>
    setLocalValues((prev) => ({
      ...prev,
      tasks: prev.tasks.filter((t) => t.id !== taskId)
    }));

  return (
    <Modal onClose={props.onClose}>
      <div className="cardinfo_container">
        <div className="cardinfo_close-btn" onClick={props.onClose}>
          <X size={24} />
        </div>

        {/* Заголовок и Описание */}
        <h2 className="cardinfo_title">
          <Editable defaultValue={localValues.title} onSubmit={updateField('title')} />
        </h2>
        <div className="cardinfo_description">
          <Editable defaultValue={localValues.description} onSubmit={updateField('description')} />
        </div>

        {/* Табличные поля */}
        <div className="cardinfo_table-fields">
          {/* Статус */}
          <div className="row">
            <div className="row_label">Статус</div>
            <div className="row_value cardinfo_static-value">{localValues.status}</div>
          </div>

          {/* Дата начала */}
          <div className="row">
            <div className="row_label">Дата начала</div>
            <div className="row_value date-picker">
              <input
                type="date"
                value={localValues.startDate}
                onChange={(e) => updateField('startDate')(e.target.value)}
              />
            </div>
          </div>

          {/* Дата завершения */}
          <div className="row">
            <div className="row_label">Дата завершения</div>
            <div className="row_value date-picker">
              <input
                type="date"
                value={localValues.dueDate}
                onChange={(e) => updateField('dueDate')(e.target.value)}
              />
            </div>
          </div>

          {/* Приоритет */}
          <div className="row">
            <div className="row_label">Приоритет</div>
            <div className="row_value cardinfo_static-value" style={priorityStyle}>
              {priorityLabel}
            </div>
          </div>
        </div>

        {/* Чеклист */}
        <div className="cardinfo_checklist-header">
          <h3>Чеклист ({tasksCompleted}/{tasksCount})</h3>
        </div>
        <div className="cardinfo_progress-bar">
          <div className="cardinfo_progress-fill" style={{ width: `${progressPercent}%` }} />
        </div>
        <div className="cardinfo_tasks">
          {localValues.tasks.map((t) => (
            <div key={t.id} className="cardinfo_task-item">
              <label className="custom-checkbox">
                <input type="checkbox" checked={t.completed} onChange={() => toggleTask(t.id)} />
                <span className="checkmark" />
              </label>
              <span className={t.completed ? 'done' : ''}>{t.text}</span>
              <button type="button" className="cardinfo_task-remove" onClick={() => removeTask(t.id)}>
                <Trash size={14} />
              </button>
            </div>
          ))}
          <Editable defaultValue="Добавить задачу" placeholder="Введите задачу..." onSubmit={addTask} />
        </div>
      </div>
    </Modal>
  );
}

export default CardInfo;
